export const validate = (userData, errors, setErrors) => {
  if (!userData.email) {
    setErrors({ ...errors, email: "Email vacío" });
  } else if (userData.email.length > 35) {
    setErrors({ ...errors, email: "Email no puede contener más de 35 caracteres" });
  } else {
    setErrors({ ...errors, email: "" });
  }

  if (!userData.password) {
    setErrors({ ...errors, password: "Contraseña vacía" });
  } else if (userData.password.length < 6 || userData.password.length > 10) {
    setErrors({ ...errors, password: "Contraseña debe tener entre 6 y 10 caracteres" });
  } else if (!/\d/.test(userData.password)) {
    setErrors({ ...errors, password: "Contraseña debe contener al menos un dígito" });
  } else {
    setErrors({ ...errors, password: "" });
  }
}
