import React, { useState } from "react";

const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

const validate = (userData, errors, setErrors) => {
    if(!userData.email) setErrors({ ...errors, email: "Email vacio"})
    else { 
        if(userData.email.length > 35) setErrors({ ...errors, email: "Email no puede contener mas de 35 caracteres"})
            else setErrors({ ...errors, email: "" });
        if(regexEmail.test(userData.email)) setErrors({ ...errors, email: ""})
        else { setErrors({ ...errors, email: "Email invalido"});
        }
    }
}

const Form = () => {

    const [userData, setUserData] = useState({
        email: "",
        password: ""
    });

    const [errors, setErrors] = useState({});

    const handleChange = (event) => {
        setUserData({
            ...userData, [event.target.name]: event.target.value
        });
        validate({
            ...userData, [event.target.name]: event.target.value
        }, errors, setErrors)
    }

    return (
    <>
        <h2>Login</h2>
        <br />
        <form action="">
        <label htmlFor="email">Email: </label>
        <input type="text" name="email" placeholder="Ingresa tu email"
         value={userData.email} onChange={handleChange} />
        <br />
        <br />
        <label htmlFor="password">Password: </label>
        <input type="text" name="password" placeholder="Ingresa tu password"
         value={userData.password} onChange={handleChange} />
        <br />
        <button>Submit</button>
        </form>
    </>
    )
}

export default Form;
