import style from './Card.module.css';
import { Link } from "react-router-dom";
import { addFav, removeFav } from '../redux/actions';
import { connect } from 'react-redux';
import { useState, useEffect } from 'react';

function Card({ id, name, status, species, gender, origin, image, onClose, addFav, removeFav, myFavorites}) {



const [isFav, setIsFav] = useState(false);


const handleFavorite = () => {
   if (isFav) {
     setIsFav(false);
     removeFav(id);
   } else {
     setIsFav(true);
     addFav({ id, name, status, species, gender, origin, image, onClose });
   }
 };

 useEffect(() => {
   if (!id) {
     return; // No hagas nada si id no est치 definido
   }

   // Verificar si el personaje est치 en favoritos
   const isCharacterInFavorites = myFavorites.some((fav) => fav.id === id);
   setIsFav(isCharacterInFavorites);
 }, [id, myFavorites]);

 if (!id) {
   return null; // O puede renderizar un mensaje de error o componente alternativo
 }


   return (
      <div className={style.div}>
         {
         isFav ? (
          <button onClick={handleFavorite}>仇벒잺</button>
             ) : (
         <button onClick={handleFavorite}>游밼</button>
         )
      }
         <button className={style.button} onClick={() => onClose(id)}>X</button>
         <Link to={`/detail/${id}`}>
            <h2 className={style.h2}>Name: {name}</h2>
         </Link>
         <h2 className={style.h2}>Status: {status}</h2>
         <h2 className={style.h2}>Species: {species}</h2>
         <h2 className={style.h2}>Gender: {gender}</h2>
         <h2 className={style.h2}>Origin: {origin}</h2>
         <img className={style.img} src={image} alt='' />
      </div>
   );

}

const mapDispatchToProps = (dispatch) => {
   return {
      addFav: (character) => {
         dispatch(addFav(character));
      },
      removeFav: (id) => {
         dispatch(removeFav(id));
      }
   };
};

const mapStateToProps = (state) => {
   return {
      myFavorites: state.myFavorites
   }
}


export default connect(mapStateToProps, mapDispatchToProps)(Card);
