import { ADD_FAV, FILTER, ORDER, REMOVE_FAV } from "./actions";

const initialState = {
    myFavorites: [],
    allCharacters: [],
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {

        case ADD_FAV:
            return {
                ...state,
                myFavorites: [...state.myFavorites, action.payload],
                allCharacters: [...state.myFavorites, action.payload]
            };

        case REMOVE_FAV:
            return {
                ...state,
                myFavorites: state.myFavorites.filter(
                    (char) => char.id !== action.payload
                )
            };

        case FILTER:
            const allCharsFiltered = state.allCharacters.filter(
                (char) => char.gender === action.payload
            );
            return {
                ...state,
                myFavorites: allCharsFiltered
            };

            case ORDER:
                let allCharsOrdered;
                if (action.payload === "A") {
                  allCharsOrdered = [...state.allCharacters].sort((a, b) => a.id - b.id);
                } else if (action.payload === "D") {
                  allCharsOrdered = [...state.allCharacters].sort((a, b) => b.id - a.id);
                } else {
                  allCharsOrdered = [...state.myFavorites];
                }
                return {
                  ...state,
                  allCharacters: allCharsOrdered,
                };

        default:
            return state;
    }
};

export default rootReducer;
