import { ADD_FAV, FILTER, ORDER, REMOVE_FAV } from "./actions";

const initialState = {
    myFavorites: [],
    allCharacters: [],
};

const rootReducer = (state = initialState, action) => {

    const { allCharacters } = state;

    switch (action.type) {

        case ADD_FAV:
            return {
                ...state,
                myFavorites: [...state.myFavorites, action.payload],
                allCharacters: [...state.myFavorites, action.payload]
            }

        case REMOVE_FAV:
            return {
                ...state, 
                myFavorites: state.myFavorites.filter(
                    (char) => char.id !== action.payload
                )
            }

        case FILTER:
            // return {
            //     ...state,
            //     allCharacters: [...state.allCharacters.filter(
            //         (char) => {
            //             return char.gender === action.payload
            //         }
            //     )],
            //     myFavorites: state.allCharacters
            // }

            const allCharsFiltered = allCharacters.filter(char => char.
                gender === action.payload);
                return {
                    ...state,
                    myFavorites: allCharsFiltered
                }

        case ORDER:
            const allCharsOrdered = allCharacters.sort((a, b) => {
                if (action.payload === "A") return a.id - b.id;
                else if (action.payload === "D") return b.id - a.id;
                else return 0;
            });
            return {
                ...state,
                myFavorites: allCharsOrdered
            };       

        default:
            return { ...state};

    }
}

export default rootReducer;
